import sys
import importlib

logger = system.util.getLogger("TagEvent_OEE")

try:
    # Delete cached modules under Project.OEE_NEW
    for module_name in list(sys.modules.keys()):
        if module_name.startswith("Project.OEE_NEW"):
            del sys.modules[module_name]

    # Re-import using Project namespace
    import Project.OEE_NEW as OEE_NEW

    # Reload all submodules/scripts
    for attr_name in dir(OEE_NEW):
        attr = getattr(OEE_NEW, attr_name)
        try:
            importlib.reload(attr)
        except Exception as e:
            # Log submodule reload errors
            logger.error("Failed to reload submodule '{}': {}".format(attr_name, e))

    logger.info("OEE_NEW package reloaded successfully.")

except Exception as e:
    # Log any top-level error
    logger.error("Error reloading OEE_NEW package: {}".format(e))
